contentCss:
body {
color:white;
  font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, Oxygen, Ubuntu, Cantarell, 'Open Sans', 'Helvetica Neue', sans-serif;
  line-height: 1.4;
  margin: 1rem;
}


table {
  border-collapse: collapse;
}

table th,
table td {
  border: 1px solid #ccc;
  padding: .4rem;
}


figure {
  display: table;
  margin: 1rem auto;
}

figure figcaption {
  color: #999;
  display: block;
  margin-top: .25rem;
  text-align: center;
}

hr {
  border-color: #ccc;
  border-style: solid;
  border-width: 1px 0 0 0;
}

code {
  background-color: #e8e8e8;
  border-radius: 3px;
  padding: .1rem .2rem;
}

.mce-content-body:not([dir=rtl]) {
  blockquote {
    border-left: 2px solid #ccc;
    margin-left: 1.5rem;
    padding-left: 1rem;
  }
}

// RTL
.mce-content-body[dir=rtl] {
  blockquote {
    border-right: 2px solid #ccc;
    margin-right: 1.5rem;
    padding-right: 1rem;
  }
}

initConfig:
tinymce.init({
  menubar: false,
        plugins: 'lists, link, fullscreen, visualblocks', 
        //plugins: 'quickbars',
        toolbar: 'undo redo | h1 h2 | bold italic | bullist numlist outdent indent | link | fullscreen' ,
  height: 360
});

lessVariables:
@background-color: rgba(136, 136, 136, 1);
@toolbar-background-color: rgba(136, 136, 136, 1);
@menubar-background-color: rgba(136, 136, 136, 1);
@border-color: rgba(255, 255, 255, 1);
@color-black: rgba(255, 255, 255, 1);
@color-white: rgba(136, 136, 136, 1);
@color-tint: rgba(255, 210, 51, 1);
@color-error: #c00;
@color-success: #78AB46;
@base-value: 16px;
@control-border-radius: 15px;
@panel-border-radius: 15px;
@tinymce-border-radius: 5px;
@font-stack: -apple-system,BlinkMacSystemFont,"Segoe UI",Roboto,Oxygen-Sans,Ubuntu,Cantarell,"Helvetica Neue",sans-serif;
@font-weight-normal: normal;
@font-weight-bold: bold;
@toolbar-button-icon-color: rgba(255, 255, 255, 1);
@toolbar-button-text-color: rgba(255, 255, 255, 1);
@toolbar-button-hover-background-color: rgba(255, 210, 51, 1);
@toolbar-button-hover-icon-color: @toolbar-button-icon-color;
@toolbar-button-hover-text-color: @toolbar-button-icon-color;
@toolbar-button-focus-background-color: rgba(255, 210, 51, 1);
@toolbar-button-focus-icon-color: @toolbar-button-hover-icon-color;
@toolbar-button-focus-text-color: @toolbar-button-hover-icon-color;
@toolbar-button-active-background-color: rgba(255, 210, 51, 1);
@toolbar-button-enabled-background-color: rgba(255, 210, 51, 1);
@toolbar-button-active-icon-color: rgba(29, 24, 4, 1);
@toolbar-button-active-text-color: rgba(29, 24, 4, 1);
@toolbar-button-enabled-icon-color: rgba(29, 24, 4, 1);
@toolbar-button-enabled-text-color: rgba(29, 24, 4, 1);
